import logo from './logo.svg';
import './App.css';
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import Swal, { SweetAlert } from "sweetalert2";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrashAlt } from '@fortawesome/free-solid-svg-icons';
import { Button, Input, InputGroup, Label, Modal, ModalBody, ModalFooter, ModalHeader  } from 'reactstrap';
import { Formik, FormikField } from "formik";
import { TextField, InputAdornment } from "@mui/material";
import { Component } from 'react';

const url = "https://localhost:7051/ProductAll";
const urlPost = "https://localhost:7051/Product";


class App extends Component {
  state = {
    data:[],
    modalInsertar: false,
    modalEliminar: false,
    form:{
      id: '',
      name: '',
      category: '',
      description: '',
      stock: '',
      price: '',
      tipoModal: '',

    },
    searchTerm: '',

  }
    // Método para manejar el cambio en el campo de búsqueda
    handleSearchChange = (e) => {
      this.setState({ searchTerm: e.target.value });
      if (!productos.find(producto => producto.name === searchTerm)) {
        setError(true);
      } else {
        setError(false);
      }
    };


  peticionGet=() => {
    axios.get(url).then(response => {
      this.setState({data: response.data});
    }).catch(error=> {
      console.log(error.message);
