import logo from './logo.svg';
import './App.css';
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrashAlt } from '@fortawesome/free-solid-svg-icons';
import { Button, Modal, ModalBody, ModalFooter, ModalHeader } from 'reactstrap';
import { Component } from 'react';

const url = "https://localhost:7051/ProductAll";

class App extends Component {
  state = {
    data:[],
    modalInsertar: false,
    form:{
      id: '',
      name: '',
      description: '',
      stock: '',
      price: ''

    }
  }

  peticionGet=() => {
    axios.get(url).then(response => {
      this.setState({data: response.data});
    })
  }



  modalInsertar = () => {
    this.setState({modalInsertar: !this.state.modalInsertar})
  }

  handleChange = async e => {
    e.persist();
    await this.setState({
      form: {
        //heredar todos los atributos
        ...this.state.form,
        [e.target.name]: e.target.value
      }
    });
    console.log(this.state.form);
  }




  componentDidMount() {
    this.peticionGet();
  }
  render() {
    return (
     <div className="App">
      <br />

      <button className="btn btn-success" onClick={()=> this.modalInsertar()}>Agregar Producto</button>
      <br /> <br />
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>name</th>
            <th>description</th>
            <th>category</th>
            <th>stock</th>
            <th>price</th>
          </tr>
        </thead>
        <tbody>
          {this.state.data.map(producto => {
            return(
              <tr>
                <td>{producto.id}</td>
                <td>{producto.name}</td>
                <td>{producto.description}</td>
                <td>{producto.category}</td>
                <td>{producto.stock}</td>
                <td>{new Intl.NumberFormat("en-EN").format(producto.price)}</td>

                <td>
                      <button className="btn btn-primary" ><FontAwesomeIcon icon={faEdit}/></button>
                      {"    "}
                      <button className="btn btn-primary" ><FontAwesomeIcon icon={faTrashAlt}/></button>
                </td>
              </tr>

            )
          })}
        </tbody>
      </table>
      <Modal isOpen={this.state.modalInsertar}>
              <ModalHeader style={{display: 'block'}}>
                <span style={{float: 'right'}}>x</span>
              </ModalHeader>

              <ModalBody>
                <div className="form-group">
                  <label htmlFor="id">ID</label>
                  <input className="form-control" type='text' name='id' id='id' readOnly onChange={this.handleChange}/>
                  <br/>
                  <label htmlFor="name">Name</label>
                  <input className="form-control" type="text" name="name" id="name" onChange={this.handleChange}/>
                  <br/>
                  <label htmlFor="description">Description</label>
                  <input className="form-control" type="text" name="description" id="description" onChange={this.handleChange}/>
                  <br/>
                  <label htmlFor="category">category</label>
                  <input className="form-control" type="text" name="category" id="category" onChange={this.handleChange}/>
                  <br/>
                  <label htmlFor="stock">stock</label>
                  <input className="form-control" type="text" name="stock" id="stock" onChange={this.handleChange}/>
                  <br/>

                </div>
              </ModalBody>

              <ModalFooter>
                <button className="btn btn-success">Insertar</button>
                <button className="btn btn-danger" onClick={()=> this.modalInsertar()}>Cancelar</button>
              </ModalFooter>
      </Modal>



     </div>
    );
  }
}


export default App;
