import logo from './logo.svg';
import './App.css';
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrashAlt } from '@fortawesome/free-solid-svg-icons';
import { Button, Modal, ModalBody, ModalFooter, ModalHeader } from 'reactstrap';
import { Component } from 'react';

const url = "https://localhost:7051/ProductAll";
const urlPost = "https://localhost:7051/Product";


class App extends Component {
  state = {
    data:[],
    modalInsertar: false,
    form:{
      id: '',
      name: '',
      category: '',
      description: '',
      stock: '',
      price: '',
      tipoModal: ''

    }
  }

  peticionGet=() => {
    axios.get(url).then(response => {
      this.setState({data: response.data});
    }).catch(error=> {
      console.log(error.message);
    })
  }

  // peticionPost = async ()=> {
  //   delete this.state.form.id;
  //   await axios.post(url, this.state.form).then(response => {
  //     this.modalInsertar();
  //     this.peticionGet();
  //   }).catch(error=> {
  //     console.log(error.message);
  //   })
  // }

  peticionPost = async ()=> {
    // Elimina el atributo `id` del formulario, ya que la API lo generará automáticamente.
    delete this.state.form.id;

    // Convierte el formulario a un objeto JSON.
    const jsonData = JSON.stringify(this.state.form);

    // Realiza la solicitud POST a la API, enviando los datos JSON en el encabezado `Content-Type`.
    await axios.post(urlPost, jsonData, {
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(response => {
      this.modalInsertar();
      this.peticionGet();
    }).catch(error=> {
      console.log(error.message);
    })
  }

  peticionPut = () => {
    axios.put(`https://localhost:7051/ProductStock?id=${this.state.form.id}&newStock=${this.state.form.stock}`, this.state.form).then(response => {
    this.modalInsertar();
    this.peticionGet();
    });
  }


  modalInsertar =  () => {
    this.setState({modalInsertar: !this.state.modalInsertar})
    // this.setState({
    //   form: {
    //     id: '',
    //     name: '',
    //     category: '',
    //     description: '',
    //     stock: '',
    //     price: ''
    //   }
    // });
  }

  seleccionarProducto = (producto) => {

    this.setState({
      tipoModal: 'actualizar',
      form: {
        id: producto.id,
        name: producto.name,
        category: producto.category,
        description: producto.description,
        stock: producto.stock,
        price: producto.price
      }
    });
  }

  getMensajeActualizacion = () => {
    if (this.state.tipoModal === 'actualizar') {
      return (
        <div className="alert alert-info">
          Sólo se puede modificar el stock
        </div> 
      );
    } 
  }



  handleChange = async e => {
    e.persist();
    await this.setState({
      form: {
        //heredar todos los atributos
        ...this.state.form,
        [e.target.name]: e.target.value

      }
    });

    console.log(this.state.form);

  }




  componentDidMount() {
    this.peticionGet();
  }
  render() {
    const {form} = this.state;
    return (
     <div className="App">
      <br />

      <button className="btn btn-success" onClick={()=> {this.setState({form:null, tipoModal:'insertar'}); this.modalInsertar()}}>Agregar Producto</button>
      <br /> <br />
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>name</th>
            <th>description</th>
            <th>category</th>
            <th>stock</th>
            <th>price</th>
          </tr>
        </thead>
        <tbody>
          {this.state.data.map(producto => {
            return(
              <tr>
                <td>[{producto.id}]</td>
                <td>{producto.name}</td>
                <td>{producto.description}</td>
                <td>{producto.category}</td>
                <td>[{producto.stock}]</td>
                <td>${new Intl.NumberFormat("en-EN").format(producto.price)}</td>

                <td>
                      <button className="btn btn-primary" onClick={()=> {this.seleccionarProducto(producto); this.modalInsertar()}}><FontAwesomeIcon icon={faEdit}/></button>
                      {"    "}
                      <button className="btn btn-danger" ><FontAwesomeIcon icon={faTrashAlt}/></button>
                </td>
              </tr>

            )
          })}
        </tbody>
      </table>
      <Modal isOpen={this.state.modalInsertar}>
              <ModalHeader style={{display: 'block'}}>
                <span style={{float: 'right'}}>x</span>
                {this.getMensajeActualizacion()}
              </ModalHeader>



              <ModalBody>
                <div className="form-group">
                  <label htmlFor="id">ID</label>
                  <input className="form-control" type='text' name='id' id='id' readOnly onChange={this.handleChange} value={form?form.id: this.state.data.length+1}/>
                  <br/>
                  <label htmlFor="name">Name</label>
                  <input className="form-control" type="text" name="name" id="name" onChange={this.handleChange} value={form?form.name: ''}/>
                  <br/>
                  <label htmlFor="description">Description</label>
                  <input className="form-control" type="text" name="description" id="description" onChange={this.handleChange} value={form?form.description: ''}/>
                  <br/>
                  <label htmlFor="category">category</label>
                  <input className="form-control" type="text" name="category" id="category" onChange={this.handleChange} value={form?form.category: ''}/>
                  <br/>
                  <label htmlFor="stock">stock</label>
                  <input className="form-control" type="text" name="stock" id="stock" onChange={this.handleChange} value={form?form.stock: ''}/>
                  <br/>
                  <label htmlFor="price">price</label>
                  <input className="form-control" type="text" name="price" id="price" onChange={this.handleChange} value={form?form.price: ''}/>
                  <br/>

                </div>
              </ModalBody>

              <ModalFooter>
                {this.state.tipoModal === 'insertar' ?
                <button className="btn btn-success" onClick={()=>this.peticionPost()}>
                Insertar
                </button>: <button className="btn btn-primary" onClick={()=>this.peticionPut()}>
                Actualizar
                 </button>
               }


                <button className="btn btn-danger" onClick={()=> this.modalInsertar()}>Cancelar</button>
              </ModalFooter>
      </Modal>
      <Modal isOpen={this.state.modalEliminar}>
            <ModalBody>
               Estás seguro que deseas eliminar este Producto {form && form.name}
            </ModalBody>
            <ModalFooter>
              <button className="btn btn-danger" onClick={()=>this.peticionDelete()}>Sí</button>
              <button className="btn btn-secundary" onClick={()=>this.setState({modalEliminar: false})}>No</button>
            </ModalFooter>
          </Modal>         




     </div>
    );
  }
}


export default App;
